import React, { useState } from 'react';
import BBA2 from '../navbar_components/BBA2';
import JFK2 from '../navbar_components/JFK2';
import './Navbar.css';



const Navbar = () => {
  const [activeOption, setActiveOption] = useState(null);

  const handleOptionClick = (option) => {
    setActiveOption(option);
  };

  const closeMiniWindow = () => {
    setActiveOption(null);
  };

  return (
    <nav className="navbar">
      <ul className="navbar-menu">
        <li className="navbar-item">
          <button
            className={`navbar-link ${activeOption === 'option1' ? 'active' : ''}`}
            onClick={() => handleOptionClick('option1')}
          >
            FLIGHT DETAILS
          </button>
        </li>
        <li className="navbar-item">
          <button
            className={`navbar-link ${activeOption === 'option2' ? 'active' : ''}`}
            onClick={() => handleOptionClick('option2')}
          >
            FARE SUMMERY
          </button>
        </li>
        <li className="navbar-item">
          <button
            className={`navbar-link ${activeOption === 'option3' ? 'active' : ''}`}
            onClick={() => handleOptionClick('option3')}
          >
            FARE POLICY
          </button>
        </li>
        <li className="navbar-item">
          <button
            className={`navbar-link ${activeOption === 'option4' ? 'active' : ''}`}
            onClick={() => handleOptionClick('option4')}
          >
            BAGGAGE
          </button>
        </li>
      </ul>

      {activeOption === 'option1' && (
        <div className="mini-window">
          <BBA2 />
          <JFK2 />
          <Arrow />
        </div>
      )}

      {activeOption === 'option2' && (
        <div className="mini-window">
          <h3>Option 2</h3>
          <p>This is the content of Option 2 mini-window.</p>
          <button onClick={closeMiniWindow}>Close</button>
        </div>
      )}

      {activeOption === 'option3' && (
        <div className="mini-window">
          <h3>Option 3</h3>
          <p>This is the content of Option 3 mini-window.</p>
          <button onClick={closeMiniWindow}>Close</button>
        </div>
      )}

      {activeOption === 'option4' && (
        <div className="mini-window">
          <h3>Option 4</h3>
          <p>This is the content of Option 4 mini-window.</p>
          <button onClick={closeMiniWindow}>Close</button>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
